package advt;

public class swapnumbers
{
	public static void main(String[] args)
	{
		// TODO Auto-generated method stub
		int a=10,b=20,c;//variables to be used in program  
		System.out.println("Before swap:\n A="+a+"\n B="+b);//Print the result before swap
		c=a;
		a=b;
		b=c;
		System.out.println("After swap:\n A="+a+"\n B="+b);//Print the result After swap
	}

}
